struct Specimen: View {
    @ObservedObject var model: EditorModel

    @State var text = Look.current.name

    var section: EditorMode.Gallery

    var body: some View {
        Group {
            Spacer()
                .lux
                .style(.separator)
                .view

            Group {
                section == .card ?
                    DisplaySpec(model: self.model, section: section) { trait in
                        DemoCard(model: self.model, index: 2, trait: trait)
                    }
                    // .id("com.maxwell.DisplaySpec.\(section)")
                    : nil

                section == .controls ?
                    ControlPreview(model: model, section: section)
                    : nil

                section == .panel ?
                    PanelPreview(model: self.model, section: section)
                    : nil

                section == .bar ?
                    BarPreview(model: self.model, section: section)
                    : nil

                section == .inputBar ?
                    InputBarPreview(model: self.model, section: section)
                    : nil
            }
            //            .lux.look(refreshId: "gallery.item").view
            Group {
                section == .button ?
                    ButtonPreview(model: self.model, section: section)

                    : nil

                section == .textfield ?
                    TextFieldPreview(model: self.model, section: section)
                    : nil

                section == .paragraph ?
                    ParagraphPreview(model: self.model, section: section)
                    : nil

                section == .icon ?
                    IconPreview(model: self.model, section: section)
                    : nil

                section == .spex ?
                    SpecOverview(model: self.model)
                    .frame(maxWidth: 1024)
                    : nil
            }
            //            .lux.look(refreshId: "gallery.item").view
        }
    }
}

