struct DisplaySpec<Content: View>: View {
    typealias ContentClosure = (Trait) -> Content

    @ObservedObject var model: EditorModel

    var section: EditorMode.Gallery = .card

    let content: ContentClosure
    let preferredSurfaces: [Look.Surface]?

    public init(model: EditorModel, section: EditorMode.Gallery, surfaces preferredSurfaces: [Look.Surface]? = nil, @ViewBuilder content: @escaping ContentClosure) {
        self.content = content
        self.model = model
        self.preferredSurfaces = preferredSurfaces
        self.section = section
    }

    var trait: Trait {
        model.trait
    }

    var layouts: [Trait] {
        [
            trait.tweak(.bodyLayout),
        ].reversed()
    }

    var surfaces: [Look.Surface] {
        guard model.controlPreviewSurface == nil else {
            return defaultsurfaces
        }
        return preferredSurfaces ?? defaultsurfaces
    }

    var defaultsurfaces: [Look.Surface] {
        [
            .canvas,
            .normal,
            .accent,
            .active,
        ].reversed()
    }

    func didTap(_ trait: Trait) {
        ATracker.track(.editor(.tapComponent))
        model.trait = trait

        model.setcomponentDetail(section: section) { trait in
            self.content(trait).lux.view
        }
    }

    var maxPreviewWidth: CGFloat {
        640
    }

    func makeContent(_ trait: Trait) -> some View {
        Row {
            self.content(trait)
                .lux
                .tweak(.edgesVertical)
                .feature(.padding)
                .view
        }
        .frame(maxWidth: maxPreviewWidth) //todo make dynamic e
    }

    var body: some View {
        ForEach(self.surfaces, id: \.self) { surfaceType in

            self.model.shouldPreview(surface: surfaceType) ?
                ForEach(self.layouts, id: \.self) { (layoutTrait: Trait) in

                    Column {
                        Spacer()
                            .frame(minHeight: self.model.layoutSpacing)

                        Row {
                            Spacer()
                            Text(layoutTrait.preferred(surface: surfaceType).specNameShort)
                                .lux
                                .tweak(.headlineLayout, .canvasSurface)
                                .tweak(.tertiaryFont)
                                .style(.paragraph)
                                .feature(.inactiveOpacity)
                                .view
                        }
                        .lux
                        .tweak(.largeTitleLayout)
                        .style(.layoutBlock)
                        .view

                        self.makeContent(layoutTrait.preferred(surface: surfaceType))

                            .onTapGesture {
                                self.didTap(layoutTrait.preferred(surface: surfaceType))
                            }
                    }
                }
                : nil
        }
    }
}

