struct ParagraphDemo: View {
    static var sampleManifest: [String]?

    var trait: Trait
    var extended = true
    var hash: Int = Look.current.hashValue

    var manifestFull: [String] {
        Look.current.displayManifest.components(separatedBy: .newlines)
    }

    var heading: String {
        manifestFull.first ?? Look.current.displayLemma
    }

    var manifest: [String] {
        var components = manifestFull

        if components.count >= 2 {
            _ = components.removeFirst()
        }

        return components
    }

    var sampleText: [String] {
        manifest // extended ? manifest : Array(manifest.prefix(min(manifest.count, 2)))
    }

    var refreshId: String {
        "\(hash)-ParagrahDemo"
    }

    var body: some View {
        Column {
            Paragraph(self.trait) {
                Text(Look.current.displayLemma)
                    .bold()
                    .lux
                    .trait(self.trait)
                    .tweak(.largeTitleLayout)
                    .style(.paragraph)
                    .view

                Text(self.heading)
                    .lux
                    .trait(self.trait)
                    .style(.paragraph)
                    .view

                ForEach(self.sampleText, id: \.self) { text in
                    Text(text)
                        .lux
                        .trait(self.trait)
                        .tweak(.bodyLayout)
                        .style(.paragraph)
                        .view
                }

                Column {
                    Look.current.hasCustomManifest == false ?
                        Group {
                            Text("“Thoroughly conscious ignorance is the prelude to every real advance in science.”")
                            Text("― James Clerk Maxwell")
                        }.lux.view :
                        Text(Look.current.displayLinkName).lux.view
                }
                .lux
                .trait(self.trait)
                .tweak(.footerLayout)
                .style(.paragraph)
                .view
            }

            self.extended == true ?
                Group {
                    ParagraphGroupDemo(trait: self.trait)
                    ParagraphBlockDemo(trait: self.trait)
                }
                : nil
        }
        //        .id(self.refreshId)
    }
}

