struct MirroringPreview: View {
    @ObservedObject var model: EditorModel
    var section: EditorMode.Gallery

    static let CustomTextField: () -> UITextField = {
        let textfield = UITextField()
        textfield.placeholder = "UITextField"
        return textfield
    }

    var body: some View {
        DisplaySpec(model: self.model, section: self.section, surfaces: [.normal]) { trait in
            Panel(trait) {
                HStack { Spacer() }

                VStack {
                    UIView()
                        .lux
                        .trait(trait)
                        .viewBlock { _ in
                            Text("Hello World")
                                .lux.view
                        }
                        .tweak(.largeTitleLayout)
                        .style(.paragraph)
                        .view

                    Self.CustomTextField()
                        .lux
                        .trait(trait)
                        .style(.textfieldLarge)
                        .uiViewBlock { (textField: UITextField) in
                            textField.textColor = trait.foregroundColor.uiColor()
                            textField.font = trait.fontType
                        }
                        .look(refreshId: "spex-textfield")
                        .view

                    UILabel()
                        .lux
                        .trait(trait)
                        .uiViewBlock { (label: UILabel) in
                            label.text = "UIKit Mirroing Demo"
                            label.textColor = trait.foregroundColor.uiColor()
                            label.textAlignment = .center
                            label.font = trait.fontType
                        }
                        .tweak(.footerLayout)
                        .style(.paragraph)
                        .look(refreshId: "spex-label")
                        .view
                }
            }
        }
    }
}
