extension FireConfig {
    class Publisher: ObservableObject {
        var hash = UUID().uuidString
    }
}

class FireConfig {
    static let shared = Publisher()
    static let remote = RemoteConfig.remoteConfig()
    static let TotalConfigKeys = 46
    static func launchSetup() {
        #if DEBUG
            let settings = RemoteConfigSettings()
            settings.minimumFetchInterval = 0
            remote.configSettings = settings
        #endif
        refresh()
    }

    static var DefaultConfig: NSDictionary? {
        var nsDictionary: NSDictionary?
        if let path = Bundle.main.path(forResource: "MaxwellConfig", ofType: "plist") {
            nsDictionary = NSDictionary(contentsOfFile: path)
        }
        return nsDictionary
    }

    static func refresh() {
        remote.setDefaults(DefaultConfig as? [String: NSObject])
        remote.fetchAndActivate { status, error in
            assert(error == nil)
            assert(status != .error)
            shared.hash = UUID().uuidString
        }
    }
}

