extension FireConfig {
    static let platform = Platform()
    struct Platform {
        let iOSMixpanelDebug = "iOSMixpanel"
        let macOSMixpanelDebug = "macOSMixpanel"
        let iOSMixpanelProduction = "iOSMixpanelProduction"
        let macOSMixpanelProduction = "macOSMixpanelProduction"

        let iOSFirebase = "GoogleService-Info"
        let macOSFirebase = "GoogleService-Info-macOS"

        let cloudinaryCloudNameKey = "cloudinaryCloudNameKey"
        let cloudinaryTokenKey = "cloudinaryToken"

        var mixpanelToken: String {
            #if DEBUG
                return UIApplication.isMacCatalyst ?
                    FireConfig.getValue(macOSMixpanelDebug)
                    : FireConfig.getValue(iOSMixpanelDebug)
            #else
                return UIApplication.isMacCatalyst ?
                    FireConfig.getValue(macOSMixpanelProduction)
                    : FireConfig.getValue(iOSMixpanelProduction)
            #endif
        }

        var cloudinaryCloudName: String {
            FireConfig.getValue(cloudinaryCloudNameKey)
        }

        var cloudinaryToken: String {
            FireConfig.getValue(cloudinaryTokenKey)
        }

        var cloudinaryUGCPreset: String {
            FireConfig.getValue("cloudinaryUGCPreset")
        }

        var cloudinaryLuxPreset: String {
            FireConfig.getValue("cloudinaryLuxPreset")
        }

        var cloudinaryLicensePreset: String {
            FireConfig.getValue("cloudinaryLicensePreset")
        }

        var firebaseConfigFile: String {
            UIApplication.isMacCatalyst ? macOSFirebase : iOSFirebase
        }

        var productPackageToken: String {
            FireConfig.getValue("productPackageToken")
        }
    }
}

extension FireConfig {
    static let content = Content()
    struct Content {
        let templatesKey = "contentTemplatesKey"
        let manifestsKey = "contentManifestsKey"
        let brandsKey = "contentBrandedKey"
        let codeSamplesKey = "contentCodeSamplesKey"

        var templates: [Look] {
            guard let jsonString = remote.configValue(forKey: templatesKey).stringValue else {
                return []
            }
            return Codec.object(fromJSON: jsonString) ?? []
        }

        var manifests: [Look] {
            guard let jsonString = remote.configValue(forKey: manifestsKey).stringValue else {
                return []
            }
            return Codec.object(fromJSON: jsonString) ?? []
        }

        var brands: [Look] {
            guard let jsonString = remote.configValue(forKey: brandsKey).stringValue else {
                return []
            }
            return Codec.object(fromJSON: jsonString) ?? []
        }

        var codeSamples: [String: String] {
            guard let jsonString = remote.configValue(forKey: codeSamplesKey).stringValue else {
                return [:]
            }
            return Codec.object(fromJSON: jsonString) ?? [:]
        }
    }
}
