import Alamofire
import Cloudinary
import Combine
import Foundation
import MessageUI
import StoreKit
import Zip

/// Licensing Flow
/// - Configure Contract
/// - Acquire payment
/// - Create Entitlements( contract, payment)
/// - Create License( entitlements )
/// - Download Release,  Sign and Upload Product
/// - Return Licensed Product URL/ QRCode to end user

class LicensingModel: NSObject, ObservableObject {
    @Published var state = State()
    let defaultSession = URLSession.shared

    override init() {
        super.init()
        state.license = Self.storageDB.restore()

        FireStorage.restoreLicensing { [weak self] license in
            self?.state.license = license ?? self?.state.license
        }

        if state.hasValidLicense == true {
            state.step = .download
        }
    }
}

extension LicensingModel {
    typealias ZipProtecetedData = Data
    typealias LicensedProductURLString = String
    typealias LicensingProductURL = URL
    typealias FolderURL = URL
}

extension LicensingModel {
    var canDownloadRelease: Bool {
        state.license?.packageFolderURL != nil
    }

    var canUploadProduct: Bool {
        state.license?.productFileURL != nil
    }

    func downloadRelease(or abort: @escaping () -> Void, completion: @escaping () -> Void) {
        guard canDownloadRelease else {
            assert(false)
            abort()
            return
        }

        Download.from(url: Self.downloadURL) { [weak self] data in

            guard
                let this = self,
                let tempFolder = License.tempFolderURL(),
                let data = data else {
                assert(false)
                abort()
                return
            }

            this.state.license?.releaseFileURL = License.tempZipURL(folder: tempFolder)

            this.saveReleaseFile(data: data) { [weak self] url in

                guard
                    let this = self,
                    let url = url else {
                    assert(false)
                    abort()
                    return
                }

                this.state.license?.releaseFileURL = url

                completion()
            }
        }
    }

    func uploadProduct(or abort: @escaping () -> Void, completion: @escaping () -> Void) {
        guard
            canUploadProduct,
            let productURL = state.license?.productFileURL else {
            assert(false)
            abort()
            return
        }
        // TODO: STUFF

        guard
            let fileHandle = FileHandle(forReadingAtPath: productURL.path) else {
            assert(false)
            abort()
            return
        }

        let data = fileHandle.readDataToEndOfFile()
        fileHandle.closeFile()

        let config: (CLDUploadRequestParams) -> Void = { [weak self] in

            guard let this = self else { return }
            $0.setPublicId(this.licenseCloudName)
            $0.setTags([this.state.email ?? "email", this.state.name ?? "name", "seats\(this.state.seats)", "years\(this.state.years)"])

            $0.setContext([
                "email": this.state.email ?? "email",
                "name": this.state.name ?? "name",
                "seats": "\(this.state.seats)",
                "years": "\(this.state.years)",
                "initialDate": "\(this.state.initialDateString)",
                "finalDate": "\(this.state.finalDateString)",
            ])
        }

        Uploader.postLicensedProduct(data: data, config: config) { [weak self] urlstring in
            guard
                let this = self,
                let urlstring = urlstring else {
                assert(false)
                abort()
                return
            }
            this.state.license?.productFinalURL = URL.optional(from: urlstring)
            DispatchQueue.main.async {
                completion()
            }
        }
    }

    var licenseCloudName: String {
        "\(String("Maxwell-\(state.email ?? "")-\(state.license?.id ?? "")-\(UUID().uuid)".prefix(32))).tar.xz"
    }
}

extension LicensingModel {
    var canUnzipRelease: Bool {
        state.license?.releaseFileURL != nil
    }

    var canZipProduct: Bool {
        state.license?.packageFolderURL != nil
    }

    func unzipRelease(or abort: @escaping () -> Void, completion: @escaping () -> Void) {
        guard
            canUnzipRelease,
            let zipFile = state.license?.releaseFileURL,
            let folder = state.license?.packageFolderURL else {
            assert(false)
            abort()
            return
        }

        do {
            try Zip.unzipFile(zipFile, destination: folder, overwrite: true, password: Self.zipPassword)
        } catch {
            assert(false)
            abort()
            return
        }

        DispatchQueue.global(qos: .background).async { [weak self] in
            guard let this = self else { return }

            let licenseURL = folder.appendingPathComponent("LICENSE.TXT")
            let licenseBody = """
            \(this.state.contract)
            -------------
            \(this.state.asJSONString())
            """
            do {
                try licenseBody.write(to: licenseURL, atomically: true, encoding: .utf8)
            } catch {
                assert(false)
                abort()
            }
            DispatchQueue.main.async {
                completion()
            }
        }
    }

    func zipProduct(or abort: @escaping () -> Void, completion: @escaping () -> Void) {
        guard
            canZipProduct,
            let packageURL = state.license?.packageFolderURL,
            let productURL = License.tempZipURL(folder: packageURL)
        else {
            assert(false)
            abort()
            return
        }

        DispatchQueue.global(qos: .background).async { [weak self] in

            guard let this = self else { return }
            do {
                try Zip.zipFiles(paths: [packageURL], zipFilePath: productURL, password: nil, progress: nil)
            } catch {
                assert(false)
                abort()
                return
            }
            // TODO: STUFF
            DispatchQueue.main.async {
                this.state.license?.productFileURL = productURL
                completion()
            }
        }
    }
}

extension LicensingModel {
    var canSignPackage: Bool {
        state.license?.packageFolderURL != nil
    }

    func signPackage(or abort: @escaping () -> Void, completion: @escaping () -> Void) {
        guard
            canSignPackage,
            let folder = state.license?.packageFolderURL else {
            assert(false)
            abort()
            return
        }
        var result = true
        var files: [URL] = []
        iterateFolder(at: folder) { url in
            files.append(url)
        }

        let items = Set(files)

        DispatchQueue.global(qos: .background).async { [weak self] in

            guard let this = self else { return }

            items.forEach { result = result && this.signFile(at: $0) != false }

            DispatchQueue.main.async {
                guard result else {
                    assert(false)
                    abort()
                    return
                }

                this.state.license?.signedPackage = true
                completion()
            }
        }
    }

    func signFile(at url: URL) -> Bool? {
        guard url.path.hasSuffix(".swift") else {
            return nil
        }

        do {
            let fileHandleReader = try FileHandle(forReadingFrom: url)
            let data = fileHandleReader.readDataToEndOfFile()
            fileHandleReader.closeFile()

            guard let fileString = String(data: data, encoding: .utf8) else {
                assert(false)
                return false
            }

            let contract = state.contractFileHeader
            let finalString = "\(contract)\n\(fileString)"

            let finalData = finalString.data(using: .utf8)!

            let fileHandle = try FileHandle(forWritingTo: url)
            fileHandle.seek(toFileOffset: 0)
            fileHandle.write(finalData)
            fileHandle.truncateFile(atOffset: UInt64(finalData.count))
            fileHandle.closeFile()

            DispatchQueue.main.async {
                self.update { self.state.license?.status = "Signed \(url.lastPathComponent)" }
            }

        } catch {
            assert(false)
            return false
        }

        return true
    }
}

extension LicensingModel {
    var restoredEmail: String {
        "licenseduser@restored.maxwell.design"
    }

    var restoredName: String {
        "Licensed User"
    }

    var restoredCompany: String {
        "Licensed User's Company"
    }

    var restoredProductId: String {
        AppStore.shared.purchases?.restoredPurchases.map { $0.productId }.first ?? "com.maxwelldesign.license.restored"
    }

    func prepareLicense(licensedProdutId: String?, mocking: Bool = false) {
        let email = state.email ?? state.license?.entitlements?.email ?? restoredEmail
        let name = state.name ?? state.license?.entitlements?.name ?? restoredName
        let company = state.company ?? state.license?.entitlements?.company ?? restoredCompany
        let licensedProdutId = licensedProdutId ?? state.licensedProdutId ?? state.license?.entitlements?.licensedProductId ?? restoredProductId

        let entitlements = Entitlements(
            mode: mocking ? .mockup : .normal,
            email: email,
            name: name,
            company: company,
            licensedProductId: licensedProdutId,
            contract: state.contract
        )
        state.license = License(entitlements: entitlements)
        persistLicense()
    }

    func update(_ block: @escaping () -> Void) {
        DispatchQueue.main.async {
            block()
        }
    }

    func uploadLicensedProduct(mocking _: Bool = false, completion: @escaping (License?) -> Void) {
        guard state.isUploading == false else {
            assert(false)
            return
        }
        state.isUploading = true

        func resolve() {
            persistLicense()
            DispatchQueue.main.async {
                completion(self.state.license)
            }
        }

        func finishLicensing() {
            state.isUploading = false
            guard state.license?.isReadyToDownload == true else {
                prepareLicense(licensedProdutId: state.license?.entitlements?.licensedProductId)
                resolve()
                update { [weak self] in self?.state.license?.status = "Please Retry" }
                return
            }
            resolve()
        }

        func uploadLicensedProductFlow() {
            let flow = { [weak self] in
                self?.update { self?.state.license?.status = "Decoding Bytes" }
                self?.downloadRelease(or: finishLicensing) {
                    self?.update { self?.state.license?.status = "Writing Source Code" }
                    self?.unzipRelease(or: finishLicensing) {
                        self?.update { self?.state.license?.status = "Licensing Contents" }
                        self?.signPackage(or: finishLicensing) {
                            self?.update { self?.state.license?.status = "Compressing Product" }
                            self?.zipProduct(or: finishLicensing) {
                                self?.update { self?.state.license?.status = "Storing Product" }
                                self?.uploadProduct(or: finishLicensing) {
                                    self?.update { self?.state.license?.status = "Finished Product" }
                                    finishLicensing()
                                }
                            }
                        }
                    }
                }
            }
            flow()
        }

        guard state.license != nil else {
            finishLicensing()
            return
        }

        uploadLicensedProductFlow()
    }
}

// MARK: UTILS

extension LicensingModel {
    func persistLicense() {
        Self.storageDB.store(item: state.license)
        FireStorage.storeLicensing(state.license)
    }
}

extension LicensingModel {
    func saveReleaseFile(data: Data, completion: (URL?) -> Void) {
        guard
            let releaseFileURL = state.license?.releaseFileURL,
            write(data: data, to: releaseFileURL) else {
            completion(nil)
            return
        }

        completion(releaseFileURL)
    }
}

extension LicensingModel {
    var fileManager: FileManager {
        FileManager.default
    }

    func iterateFolder(at url: FolderURL, closure: (URL) -> Void) {
        let enumerator = fileManager.enumerator(atPath: url.path)

        while
            let element = enumerator?.nextObject() as? String {
            let elementURL = url.appendingPathComponent(element)
            var isDirectory: ObjCBool = false

            fileManager.fileExists(atPath: elementURL.path, isDirectory: &isDirectory)
            if isDirectory.boolValue,
                elementURL.path != url.path {
                iterateFolder(at: elementURL, closure: closure)
            } else {
                closure(elementURL)
            }
        }
    }

    func write(data: Data, to url: URL) -> Bool {
        do {
            try data.write(to: url, options: .atomic)
            return true
        } catch {
            assert(false)
        }
        return false
    }
}

extension LicensingModel {
    func performPayment() {
        guard let product = state.selectedProduct.product else {
            assert(false)
            return
        }

        let quantity = state.selectedProduct.licensingUnits

        AppStore.shared.purchase(product: product, quantity: quantity) { [weak self] details in

            guard let productId = details?.productId else {
                return
            }
            self?.confirmPurchase(productId: productId)
        }
    }

    func confirmPurchase(productId: String) {
        prepareLicense(licensedProdutId: productId)

        DispatchQueue.main.async {
            AppStore.shared.restorePurchasesWithInfoBanners()
        }
    }

    func createAndUploadProduct() {
        prepareLicense(licensedProdutId: state.license?.entitlements?.licensedProductId)

        uploadLicensedProduct { [weak self] _ in
            self?.persistLicense()
        }
    }
}

