extension LicensingModel: MFMailComposeViewControllerDelegate {
    func mailComposeController(_: MFMailComposeViewController, didFinishWith _: MFMailComposeResult, error _: Error?) {
        UIApplication.rootVC.dismiss(animated: true)
    }
}

extension LicensingModel {
    var productFileName: String {
        "MaxwellLUX_\(state.email ?? "")v1.zip"
    }

    func sendProductByEmail() {
        guard
            MFMailComposeViewController.canSendMail(),
            let productURL = self.state.license?.productFinalURL
        else {
            shareProduct()
            return
        }

        let mail = MFMailComposeViewController()
        mail.mailComposeDelegate = self
        mail.setSubject("MAXWELL + YOU -Download")
        mail.setMessageBody("""
        Dear Artist:
        
        Please find attached the download link for your Maxwell Design Product.\n\n
        
        Download link: \(productURL.absoluteString)\n\n
        
        Notice: Remember to keep your download and license within safety measures: it contains personal and company sentive information relative to your purchase. Your support makes our open source efforts possible!
        """, isHTML: false)

        state.isUploading = true
        UIApplication.present(mail) { [weak self] in
            self?.state.isUploading = false
        }
    }

    func shareProduct() {
        guard
            let productURL = self.state.license?.productFinalURL
        else {
            assert(false)
            return
        }

        Download.from(url: productURL, fileName: productFileName, display: true) { url in
            guard let url = url else {
                assert(false)
                return
            }

            DispatchQueue.main.async {
                Sharing.activitiesFor(any: url) {
                    try? FileManager.default.removeItem(at: url)
                }
            }
        }
    }

    func saveToFile() {
        guard let productURL = self.state.license?.productFinalURL
        else {
            assert(false)
            return
        }

        FileSystem.save(fileAt: productURL, name: productFileName) { url in
            guard let url = url else { return }
            try? FileManager.default.removeItem(at: url)
        }
    }
}

extension LicensingModel {
    var step: Step {
        guard state.hasValidLicense == false else {
            return .download
        }

        return state.step
    }
}
